constants = require('../../config/constants');

const db = global.mongoDB;
const myCollection = db.collection(constants.appsTable);

exports.getApp = (companyId,appId) => {
  const query = {_id: appId, companyId: companyId};

  return new Promise((resolve, reject) => {
    myCollection.findOne(query, (err, response) => {
      if (err) {
        console.log(err);
        return reject(err);
      }
      resolve(response);
    });
  });
};