constants = require('../../config/constants');

const db = global.mongo;
const myCollection = db.collection(constants.companyTable);
const skUtil = require('../../modules/util')


exports.getCompany = (companyId) => {
  return new Promise((resolve, reject) => {
    myCollection.findOne( {_id: companyId}, async (err, response) => {
      if (err) {
        console.log(err);
        return reject(err);
      }
      resolve(await skUtil.companyRecordDecrypt(response));
    });
  });
};
