constants = require('../../config/constants');

const db = global.mongoDB;
const myCollection = db.collection(constants.connectionsTable);

const getConnection = (companyId,connectionId) => {
  const query = {_id: {connectionId, companyId}};

  return new Promise((resolve, reject) => {
    myCollection.findOne(query, (err, response) => {
      if (err) {
        console.log(err);
        return reject(err);
      }
      resolve(response);
    });
  });
};

module.exports = {
  getConnection,
}