const constants = require('../../config/constants');
const collectionName = constants.deviceUsersTable;
const db = global.mongoDB;
const myCollection = db.collection(collectionName);

exports.getDevice = (deviceId) => {
  const query = {_id: deviceId};

  return new Promise((resolve,reject) => {
    myCollection.findOne(query, (err,response) =>{
      if(err) {
        console.log (err);
        return reject(err)
      }
      resolve(response)
    })
  })
}