const constants = require('../../config/constants');

const db = global.mongoDB;
const myCollection = db.collection(constants.flowVersionsTable);

exports.getFlowVersion = (flowId,companyId,versionId) =>{
  const query = {"_id": {flowId,companyId,versionId }}
  return new Promise((resolve, reject) => {
    myCollection.findOne( query, (err, response) => {
      if (err) {
        return reject(err);
      }
      resolve(response);
    });
  });
}
