const constants = require('../../config/constants');

const db = global.mongoDB;
const myCollection = db.collection(constants.flowsTable);

exports.getFlow = (companyId,flowId) => {
  const query = {_id: {flowId, companyId}};

  return new Promise((resolve, reject) => {
    myCollection.findOne(query, (err, response) => {
      if (err) {
        console.log(err);
        return reject(err);
      }
      resolve(response);
    });
  });
};


exports.updateFlow = (companyId,flowId,props) => {

  const query = {_id: {flowId, companyId}};
  const update = {
    $set: {...props,updatedDate: Date.now()}
  };
  const options = {
    returnDocument: 'after'
  };

  return new Promise((resolve,reject) => {
    myCollection.findOneAndUpdate(query, update, options, (err,response)=>{
      if(err) {
        console.log(err);
        return reject(err);
      }
      resolve(response.value);
    })
  })
}
