constants = require('../../config/constants');

const collectionName = constants.appsTable;

exports.getApp = async (companyId,appId) => {
  const queryParams = [`${appId}`,`${companyId}`]
  const query = `
    SELECT * FROM ${collectionName} 
    WHERE _id = $1 AND companyId = $2
    ;
    `;
  try {
    const response = await global.pg.query(query,queryParams);
    if (!(response && response.rows && Array.isArray(response.rows) &&  response.rows.length >0)){
      return
    }
    return {_id:response.rows[0]._id,companyId: response.rows[0].companyid,...response.rows[0].data}
  } catch (err) {
    console.log(err)
    throw err
  }
};