constants = require('../../config/constants');

const collectionName = constants.connectionsTable;


const getConnection = async (companyId,connectionId) => {

  const queryParams = [`${connectionId}`,`${companyId}`]
  const query = `
    SELECT * FROM ${collectionName} 
    WHERE connectionId = $1 AND companyId = $2
    ;
    `;
  try {
    const response = await global.pg.query(query,queryParams);
    if (!(response && response.rows && Array.isArray(response.rows) &&  response.rows.length >0)){
      return
    }
    const finalResponse = {_id: {connectionId:response.rows[0].connectionid,companyId: response.rows[0].companyid}, ...response.rows[0].data}
    return finalResponse
  } catch (err) {
    throw err
  }

};
module.exports = {
  getConnection
}