const request = require('request')

const slackUrl = global.secrets.slack.url
exports.sendToSlack = function(channel, message) {
  const payload = {
    text: message,
    icon_emoji:':ghost'
  }
  if (channel.indexOf('#') == -1 && channel.indexOf('@') == -1) {
    payload.channel = `#${channel}`
  } else {
    payload.channel = channel
  }

  request({
    method: 'POST',
    url: slackUrl,
    headers: { 'Content-type': 'application/json' },
    body: JSON.stringify(payload),
  })
}
